(function(window){
    // Minimal Chart.js stub to avoid runtime errors when Chart.js is not available offline.
    // This stub does not render charts; it only provides a compatible constructor with a destroy() method.
    function ChartStub(ctx, config) {
        this.ctx = ctx;
        this.config = config;
        this.destroy = function() { /* no-op */ };
    }

    // Expose as global Chart if Chart isn't already loaded
    if (typeof window.Chart === 'undefined') {
        window.Chart = ChartStub;
    }
})(window);
